ALTER TABLE `oc_customer_transaction` ADD `product_id` INT(11) NOT NULL AFTER `order_id`, ADD `quantity` INT(4) NOT NULL AFTER `product_id`, ADD `price` DECIMAL(15,4) NOT NULL DEFAULT '0' AFTER `quantity`;
ALTER TABLE `oc_customer` ADD `total_balance_transaction` DECIMAL(15,4) NOT NULL DEFAULT '0' AFTER `date_added`, ADD `negative_balance_transaction` DECIMAL(15,4) NOT NULL DEFAULT '0' AFTER `total_balance_transaction`, ADD `job_balance_transaction` DECIMAL(15,4) NOT NULL DEFAULT '0' AFTER `negative_balance_transaction`, ADD `payment_code` VARCHAR(128) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL AFTER `job_balance_transaction`;